import { HttpClientModule } from '@angular/common/http';
import { TestBed } from '@angular/core/testing';
import { of } from 'rxjs';
import { AppComponent } from './app.component';
describe('AppComponent', () => {
    let component;
    let fixture;
    beforeEach(async () => {
        await TestBed.configureTestingModule({
            declarations: [AppComponent],
            imports: [HttpClientModule]
        })
            .compileComponents();
    });
    beforeEach(() => {
        fixture = TestBed.createComponent(AppComponent);
        component = fixture.componentInstance;
        fixture.detectChanges();
    });
    it('should create the app', () => {
        fixture = TestBed.createComponent(AppComponent);
        component = fixture.componentInstance;
        expect(component).toBeTruthy();
    });
    it('Should fetch all the users', () => {
        const mockUsers = [{
                id: 1,
                name: "Leanne Graham",
                username: "Bret",
                email: "Sincere@april.biz",
            }];
        spyOn(component.dataservice, 'getUsers').and.returnValue(of(mockUsers));
        component.fetchUsers();
        expect(component.isLoaded).toBeTrue();
    });
    it('Should get error while fetching the users', () => {
        const error = new Error();
        spyOn(component.dataservice, 'getUsers').and.returnValue(of(error));
        component.fetchUsers();
        expect(component.isLoaded).toBeTrue();
    });
    it('Should fetch posts by selected user', () => {
        const mockPosts = [{
                "userId": 1,
                "id": 1,
                "title": "sunt aut facere repellat provident occaecati excepturi optio reprehenderit",
                "body": "quia et"
            }];
        spyOn(component.dataservice, 'fetchUserPost').and.returnValue(of(mockPosts));
        const mockObj = {
            "id": 1,
            "name": "Leanne Graham",
            "username": "Bret",
            "email": "Sincere@april.biz",
        };
        component.fetchPost(mockObj);
        expect(component.isLoaded).toBeTrue();
    });
    it('Should get error while fetching posts by the selected users', () => {
        const error = new Error();
        spyOn(component.dataservice, 'fetchUserPost').and.returnValue(of(error));
        const mockObj = {
            "id": 1,
            "name": "Leanne Graham",
            "username": "Bret",
            "email": "Sincere@april.biz",
        };
        component.fetchPost(mockObj);
        expect(component.isLoaded).toBeTrue();
    });
    it('Should fetch comments for the selected posts', () => {
        const mockComments = [{
                postId: 1,
                id: 2,
                name: "quo vero reiciendis velit similique earum",
                email: "Jayne_Kuhic@sydney.com",
                body: "est natus enim"
            }];
        spyOn(component.dataservice, 'fetchComments').and.returnValue(of(mockComments));
        const mockObj = {
            "userId": 1,
            "id": 1,
            "title": "sunt aut facere repellat provident occaecati excepturi optio reprehenderit",
            "body": "quia et",
        };
        component.fetchComments(mockObj);
        expect(component.isLoaded).toBeTrue();
    });
    it('Should get error while fetching comments for the selected posts', () => {
        const error = new Error();
        spyOn(component.dataservice, 'fetchComments').and.returnValue(of(error));
        const mockObj = {
            "userId": 1,
            "id": 1,
            "title": "sunt aut facere repellat provident occaecati excepturi optio reprehenderit",
            "body": "quia et",
        };
        component.fetchComments(mockObj);
        expect(component.isLoaded).toBeTrue();
    });
    it('Should load all post when click on Load All button', () => {
        component.loadAllPosts();
        expect(component.loadAll).toBeTrue();
    });
});
